declare module "*.css" {
  const css: { [key: string]: string };
  export default css;
}

declare module "*.png" {
  const value: any;
  export = value;
}

declare module "*.jpg" {
  const value: any;
  export = value;
}

declare module "*.gif" {
  const value: any;
  export = value;
}

declare module "*.md" {
  const value: any;
  export = value;
}
